      const payload = this.buildShipmentPayload();
      const manifestUpdates = Object.values(this.manifestEditsById || {});
      const updateManifests$ = manifestUpdates.length ? forkJoin(manifestUpdates.map(m => this.http.put(`http://localhost:3000/api/manifest/${m._id}`, m))) : of([]);
      updateManifests$.subscribe({
        next: () => {
          this.http.put(`http://localhost:3000/api/newshipments/${payload.consignmentNumber}`, payload).subscribe({
