      const prefix = hasBaskets ? `$$${this.buildBasketDescriptor()}$$ ` : '$$All$$ ';
      return prefix + this.shipmentRoute.map(p => {
        const vehicle = String(p.vehicleNo || '').trim();
        return vehicle ? `${p.name} (${p.type})|${vehicle}` : `${p.name} (${p.type})`;
      }).join(' -> ');
