        const parts = key.split('|');
        const labelType = parts.shift() || '';
        const labelValue = parts.join('|');
        const label = labelValue ? `${labelType}:${labelValue}` : labelType;
        return `${label}-${Number(b.qty)}`;
      }).join('|');
