    // CONSIGNMENT NUMBER
    getCurrentConsignmentNumber() {
      if (!this.branch || this.branch === 'All Branches') {
        this.consignmentNumber = 'nil';
        return;
      }
      this.http.get(`http://localhost:3000/api/newshipments/nextConsignment?username=${encodeURIComponent(this.username)}&branch=${encodeURIComponent(this.branch)}`).subscribe({
        next: res => {
          this.consignmentNumber = res.nextNumber.toString();
