        const currentBranchId = this.normalizeId(stock?.currentLocationId || stock?.currentBranchId || stock?.currentBranch);
        const currentBranchLabel = String(stock?.currentBranch || '').trim();
        return currentBranchId === hubId || hasHubName && currentBranchLabel.toLowerCase() === hubName.toLowerCase() || hasHubName && this.matchesBranchLabel(currentBranchLabel, hubName);
      }
      if (this.activeTab === 'other-branch') {
        const originBranchId = this.normalizeId(stock?.branchId || stock?.branch);
        const originBranchLabel = String(stock?.branch || '').trim();
        return originBranchId === hubId || hasHubName && originBranchLabel.toLowerCase() === hubName.toLowerCase() || hasHubName && this.matchesBranchLabel(originBranchLabel, hubName);
      }
      if (this.activeTab === 'others-in-branch') {
        const currentBranchId = this.normalizeId(stock?.currentLocationId || stock?.currentBranchId || stock?.currentBranch);
        const currentBranchLabel = String(stock?.currentBranch || '').trim();
        return currentBranchId === hubId || hasHubName && currentBranchLabel.toLowerCase() === hubName.toLowerCase() || hasHubName && this.matchesBranchLabel(currentBranchLabel, hubName);
