    }
    editStock(stock) {
      console.log('?o??,? Edit stock:', stock);
      const cloned = JSON.parse(JSON.stringify(stock));
      cloned.invoices = this.flattenInvoices(cloned.ewaybills || cloned.invoices || []);
      this.editingStock = cloned;
      this.manifestEditsById = {};
