      if (value < 0) value = 0;
      line.newManifestQty = value;
      const pending = this.pendingManifestAdjustment;
      if (!pending) return;
      const totals = this.getManifestTotals();
      pending.targetAmount = (Number(pending.popupInstock) || 0) + totals.manifestTotal;
      if (pending.productRef) {
