        originType: 'branch',
        originLocId: normalized
      } : null;
    }
    normalizeHubId(value) {
      if (!value) return '';
      if (typeof value === 'string') return value;
      if (value?._id) return String(value._id);
      if (value?.$oid) return String(value.$oid);
      return String(value);
    }
    loadHubDetails() {
      this.http.get('http://localhost:3000/api/hubs').subscribe({
        next: hubs => {
          this.hubs = hubs || [];
          this.rebuildRateAddressOptions();
          if (this.deliveryType === 'hub') {
            this.updateDeliveryFromHub();
          }
        },
        error: () => {
          this.hubs = [];
        }
      });
    }
    // E-WAYBILL HELPERS
    addEwaybill() {
      this.ewaybills.push({
        number: '',
        date: this.date,
        invoices: []
      });
      this.scheduleQuote();
    }
    deleteEwaybill(index) {
      this.ewaybills.splice(index, 1);
      this.scheduleQuote();
    }
    addInvoice(ewaybillIndex) {
      this.ewaybills[ewaybillIndex].invoices.push({
        number: '',
        value: 0,
        packages: [],
        products: []
      });
      this.scheduleQuote();
    }
    deleteInvoice(ewaybillIndex, invoiceIndex) {
      this.ewaybills[ewaybillIndex].invoices.splice(invoiceIndex, 1);
      this.scheduleQuote();
    }
    addPackage(ewaybillIndex, invoiceIndex) {
      this.ewaybills[ewaybillIndex].invoices[invoiceIndex].packages.push({
        type: '',
        amount: 0
      });
      this.scheduleQuote();
    }
    deletePackage(ewaybillIndex, invoiceIndex, packageIndex) {
      this.ewaybills[ewaybillIndex].invoices[invoiceIndex].packages.splice(packageIndex, 1);
      this.scheduleQuote();
    }
    addProduct(ewaybillIndex, invoiceIndex) {
      this.ewaybills[ewaybillIndex].invoices[invoiceIndex].products.push({
        type: '',
        amount: 1,
        ratePer: 0,
        instock: 1,
        intransitstock: 0,
        deliveredstock: 0
      });
