    }
    loadPricingSuggestions(clientId, requestKey, attempt = 0) {
      const originLocId = localStorage.getItem('originLocId') || 'all';
      if (!originLocId || originLocId === 'all') return;
      if (!clientId) return;
      if (requestKey !== this.suggestionRequestKey) return;
      const branch = this.branch || '';
      const params = clientId ? `originLocId=${encodeURIComponent(originLocId)}&branch=${encodeURIComponent(branch)}&clientId=${clientId}` : `originLocId=${encodeURIComponent(originLocId)}&branch=${encodeURIComponent(branch)}`;
      const routeParams = [`pickupLocationId=${encodeURIComponent(this.pickupLocationId || '')}`, `deliveryLocationId=${encodeURIComponent(this.deliveryLocationId || '')}`, `rateUnit=${encodeURIComponent(this.rateUnit)}`].join('&');
      this.http.get(`http://localhost:3000/api/pricing/suggestions?${params}&${routeParams}`).subscribe({
        next: res => {
          if (requestKey !== this.suggestionRequestKey) return;
          if (res?.pricing) {
            this.productList = res.pricing;
            this.suggestedRates = {};
            res.pricing.forEach(p => {
              if (p?.suggestedRate !== null && p?.suggestedRate !== undefined) {
                this.suggestedRates[p.productName] = Number(p.suggestedRate) || 0;
              }
            });
          }
          if (this.suggestionRetryHandle) {
            clearTimeout(this.suggestionRetryHandle);
            this.suggestionRetryHandle = null;
          }
        },
        error: () => {
          if (requestKey !== this.suggestionRequestKey) return;
          if (attempt >= this.suggestionMaxRetries) {
            return;
          }
          const delay = 500 * (attempt + 1);
          if (this.suggestionRetryHandle) {
            clearTimeout(this.suggestionRetryHandle);
          }
          this.suggestionRetryHandle = setTimeout(() => {
            this.suggestionRetryHandle = null;
            this.loadPricingSuggestions(clientId, requestKey, attempt + 1);
          }, delay);
        }
      });
    }
    getClientIdByLocationId(id) {
      if (!id) return null;
